Liqueur.create(name: "Bailey's Irish Cream", liqueur_type: "Irish Cream", description: "A cream liqueur based on Irish whiskey, cream and other flavourings. It typically has an ABV (alcohol by volume) level of 15 to 20% and is served on its own or in mixed drinks, most commonly Irish coffee" , origin: "Ireland" , flavor: "Sweet, Creamy", abv: "15-20%", color: "White")
